package code.wars.maths;

import static org.assertj.core.api.Assertions.assertThat;

import org.junit.jupiter.api.Test;

class PrimorialOfNumberTest {

    @Test
    void test1() {

        assertThat(PrimorialOfNumber.solve(3)).isEqualTo("30");
    }

    @Test
    void test2() {

        assertThat(PrimorialOfNumber.solve(5)).isEqualTo("2310");
    }

    @Test
    void test3() {

        assertThat(PrimorialOfNumber.solve(9)).isEqualTo("223092870");
    }

    @Test
    void test4() {

        assertThat(PrimorialOfNumber.solve(12)).isEqualTo("7420738134810");
    }

    @Test
    void test5() {

        assertThat(PrimorialOfNumber.solve(354)).isEqualTo(
                "");
    }
}
